import React, { useEffect, useState } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import { AuthType } from '../../services/authService'
import './index.scss'
import { useAuth, UserContextValue } from '../../providers/userProvider'
import Header from '../../components/header'


const Login = () => {
    const {authenticate, user}:UserContextValue = useAuth()
    const location = useLocation(), navigate = useNavigate()
    const [email, setEmail] = useState<string>('')
    const [password, setPassword] = useState<string>('')
    const redirectToRouteIfAny = () => {
        const state:any = location.state || {}
        if(state['from']){
            navigate(state['from'])
        } else{
            navigate('/')
        }
    }
    useEffect(() => {
        if(user){
            redirectToRouteIfAny()
        }
    },[user])
    const signUpWithGoogle = () => {
        authenticate(AuthType.Google)
    }
    return (
        <>
            <Header pageName='Login' />
            <div className='login-container container'>
                <form className='signin-form'>
                    <label className='label' htmlFor='email'>Email</label>
                    <input type='text' placeholder='Email' name='email' id='email' value={email} />
                    <label className='label' htmlFor='password'>Password</label>
                    <input type='password' placeholder='Password' name='password' id='password' value={password} />
                    <button>Sign In</button>
                </form>
                <button onClick={signUpWithGoogle}>sign up with google</button>
            </div>
        </>
    )
}

export default Login
