import { createContext, useState } from "react";
import User from "../entities/user";
import Authenticate, { AuthType } from "./authService";

interface UserContextWrapperProps{
    children: JSX.Element | null
}
interface UserContextValue{
    user: User | null;
    authenticate: (authType: AuthType) => Promise<void>
}
export const UserContextWrapper = ({children}: UserContextWrapperProps) => {
    const [user, setUser] = useState<User | null>(null)
    const authenticate = async (authType:AuthType) => {
        const result:User | null = await Authenticate(authType);
        if(result){
            setUser(result)
        }
    }
    const UserContext = createContext<UserContextValue>({
        user,
        authenticate
    })
    return (
        <UserContext.Provider value={{
            user,
            authenticate
        }}>
            {children}
        </UserContext.Provider>
    )    
}
