import React, { useEffect } from 'react'
import AlertIconSuccess from '../../images/ic_alert_success'
import AlertIcon from '../../images/ic_alert_with_white_bg'
import './index.scss'

export const SnackbarTypes = {
  error: 'error',
  success: 'success',
  info: 'info'
}

const Snackbar = ({
  type = SnackbarTypes.success,
  duration = 5000,
  children,
  hideSnackbar,
}) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      hideSnackbar()
    }, duration)
    return () => {
      clearTimeout(timer)
    }
  }, [])

  return (
    <div className="snackbar-container">
      <div className={`snackbar ${type}`}>
        {type === SnackbarTypes.success ? <AlertIconSuccess/> : <AlertIcon/>}
        {children}
      </div>
    </div>
  )
}

export default Snackbar
